from price import Pricefrom staking import *from trasactions import ServiceFees,BurnFeesfrom params import *from vesting import Vestingfrom staking import Stakingfrom supply import CircSupply"initialise the simulation"Vesting=Vesting()CircSupply=CircSupply(C0=0)for t in range(T):        #update vesting    Vesting.update()        #compute Burns    Burns.update()        #updates price    Price.update()            #Staking part:    #Locks tokens    Staking.locks()    #Unlocked tokens    Staking.unlock()        #updates circulating supply    CircSupply.update(Vesting,Burns,Staking,Price)                                    